import type { Express } from "express";
import { createServer, type Server } from "http";
import { z } from "zod";
import { storage } from "./storage";
import { insertUserSchema, insertResumeSchema, insertAtsAnalysisSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // User routes
  app.get("/api/users/:id", async (req, res) => {
    try {
      const user = await storage.getUser(req.params.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Get user error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/users", async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);
      const user = await storage.createUser({
        ...userData,
        id: req.body.id // Firebase UID
      });
      res.status(201).json(user);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid user data", errors: error.errors });
      }
      console.error("Create user error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.put("/api/users/:id", async (req, res) => {
    try {
      const updates = insertUserSchema.partial().parse(req.body);
      const user = await storage.updateUser(req.params.id, updates);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid user data", errors: error.errors });
      }
      console.error("Update user error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/users/:id/analytics", async (req, res) => {
    try {
      const analytics = await storage.getUserAnalytics(req.params.id);
      res.json(analytics);
    } catch (error) {
      console.error("Get user analytics error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Resume routes
  app.get("/api/resumes", async (req, res) => {
    try {
      const userId = req.query.userId as string;
      if (!userId) {
        return res.status(400).json({ message: "userId is required" });
      }
      const resumes = await storage.getUserResumes(userId);
      res.json(resumes);
    } catch (error) {
      console.error("Get resumes error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/resumes/:id", async (req, res) => {
    try {
      const resume = await storage.getResume(req.params.id);
      if (!resume) {
        return res.status(404).json({ message: "Resume not found" });
      }
      res.json(resume);
    } catch (error) {
      console.error("Get resume error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/resumes", async (req, res) => {
    try {
      const resumeData = insertResumeSchema.parse(req.body);
      const resume = await storage.createResume(resumeData);
      res.status(201).json(resume);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid resume data", errors: error.errors });
      }
      console.error("Create resume error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.put("/api/resumes/:id", async (req, res) => {
    try {
      const updates = insertResumeSchema.partial().parse(req.body);
      const resume = await storage.updateResume(req.params.id, updates);
      if (!resume) {
        return res.status(404).json({ message: "Resume not found" });
      }
      res.json(resume);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid resume data", errors: error.errors });
      }
      console.error("Update resume error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.delete("/api/resumes/:id", async (req, res) => {
    try {
      const success = await storage.deleteResume(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Resume not found" });
      }
      res.status(204).send();
    } catch (error) {
      console.error("Delete resume error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/resumes/:id/duplicate", async (req, res) => {
    try {
      const originalResume = await storage.getResume(req.params.id);
      if (!originalResume) {
        return res.status(404).json({ message: "Resume not found" });
      }

      const duplicateData = {
        ...originalResume,
        title: `${originalResume.title} (Copy)`,
        atsScore: null,
        atsAttempts: 0,
      };
      delete (duplicateData as any).id;
      delete (duplicateData as any).createdAt;
      delete (duplicateData as any).updatedAt;

      const duplicate = await storage.createResume(duplicateData as InsertResume);
      res.status(201).json(duplicate);
    } catch (error) {
      console.error("Duplicate resume error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // ATS Analysis routes
  app.get("/api/ats-analyses", async (req, res) => {
    try {
      const resumeId = req.query.resumeId as string;
      if (!resumeId) {
        return res.status(400).json({ message: "resumeId is required" });
      }
      const analyses = await storage.getResumeAtsHistory(resumeId);
      res.json(analyses);
    } catch (error) {
      console.error("Get ATS analyses error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/ats-analyses", async (req, res) => {
    try {
      const analysisData = insertAtsAnalysisSchema.parse(req.body);

      // Check if resume exists and user has attempts remaining
      const resume = await storage.getResume(analysisData.resumeId);
      if (!resume) {
        return res.status(404).json({ message: "Resume not found" });
      }

      if ((resume.atsAttempts || 0) >= 5) {
        return res.status(400).json({ message: "Maximum ATS analysis attempts reached" });
      }

      const analysis = await storage.createAtsAnalysis(analysisData);

      // Update resume with new ATS score and increment attempts
      await storage.updateResume(analysisData.resumeId, {
        atsScore: analysisData.score,
        atsAttempts: (resume.atsAttempts || 0) + 1,
      });

      res.status(201).json(analysis);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid analysis data", errors: error.errors });
      }
      console.error("Create ATS analysis error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Search and filtering routes
  app.get("/api/resumes/search", async (req, res) => {
    try {
      const userId = req.query.userId as string;
      const searchTerm = req.query.q as string;

      if (!userId) {
        return res.status(400).json({ message: "userId is required" });
      }
      if (!searchTerm) {
        return res.status(400).json({ message: "Search term is required" });
      }

      const resumes = await storage.searchResumes(userId, searchTerm);
      res.json(resumes);
    } catch (error) {
      console.error("Search resumes error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/templates/:templateId/resumes", async (req, res) => {
    try {
      const userId = req.query.userId as string;
      const templateId = req.params.templateId;

      if (!userId) {
        return res.status(400).json({ message: "userId is required" });
      }

      const resumes = await storage.getResumesByTemplate(userId, templateId);
      res.json(resumes);
    } catch (error) {
      console.error("Get resumes by template error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Health check endpoint
  app.get("/api/health", (req, res) => {
    res.json({ status: "ok", timestamp: new Date().toISOString() });
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type Resume, type AtsAnalysis, type InsertResume, type InsertAtsAnalysis } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser & { id: string }): Promise<User>;
  updateUser(id: string, updates: Partial<InsertUser>): Promise<User | undefined>;
  getUserAnalytics(userId: string): Promise<{
    totalResumes: number;
    avgAtsScore: number;
    templateUsage: Record<string, number>;
    mostRecentUpdate: string | null;
  }>;

  // Resume operations
  getResume(id: string): Promise<Resume | undefined>;
  getUserResumes(userId: string): Promise<Resume[]>;
  createResume(resume: InsertResume): Promise<Resume>;
  updateResume(id: string, updates: Partial<InsertResume>): Promise<Resume | undefined>;
  deleteResume(id: string): Promise<boolean>;
  searchResumes(userId: string, searchTerm: string): Promise<Resume[]>;
  getResumesByTemplate(userId: string, templateId: string): Promise<Resume[]>;

  // ATS Analysis operations
  createAtsAnalysis(analysis: InsertAtsAnalysis): Promise<AtsAnalysis>;
  getResumeAtsHistory(resumeId: string): Promise<AtsAnalysis[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private resumes: Map<string, Resume>;
  private atsAnalyses: Map<string, AtsAnalysis>;

  constructor() {
    this.users = new Map();
    this.resumes = new Map();
    this.atsAnalyses = new Map();
  }

  // User operations
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async createUser(userData: InsertUser & { id: string }): Promise<User> {
    const user: User = {
      ...userData,
      displayName: userData.displayName ?? null,
      photoURL: userData.photoURL ?? null,
      experienceLevel: userData.experienceLevel ?? null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.users.set(user.id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<InsertUser>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser: User = {
      ...user,
      ...updates,
      updatedAt: new Date(),
    };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  async getUserAnalytics(userId: string): Promise<{
    totalResumes: number;
    avgAtsScore: number;
    templateUsage: Record<string, number>;
    mostRecentUpdate: string | null;
  }> {
    const userResumes = await this.getUserResumes(userId);
    const totalResumes = userResumes.length;

    if (totalResumes === 0) {
      return {
        totalResumes: 0,
        avgAtsScore: 0,
        templateUsage: {},
        mostRecentUpdate: null,
      };
    }

    const atsScores = userResumes
      .map(resume => resume.atsScore)
      .filter((score): score is number => score !== null && score !== undefined);

    const avgAtsScore = atsScores.length > 0 
      ? Math.round(atsScores.reduce((sum, score) => sum + score, 0) / atsScores.length)
      : 0;

    const templateUsage = userResumes.reduce((acc, resume) => {
      acc[resume.templateId] = (acc[resume.templateId] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    const mostRecentUpdate = userResumes.length > 0 
      ? userResumes
          .sort((a, b) => new Date(b.updatedAt!).getTime() - new Date(a.updatedAt!).getTime())[0]
          .updatedAt!.toISOString()
      : null;

    return {
      totalResumes,
      avgAtsScore,
      templateUsage,
      mostRecentUpdate,
    };
  }

  // Resume operations
  async getResume(id: string): Promise<Resume | undefined> {
    return this.resumes.get(id);
  }

  async getUserResumes(userId: string): Promise<Resume[]> {
    return Array.from(this.resumes.values())
      .filter(resume => resume.userId === userId)
      .sort((a, b) => new Date(b.updatedAt!).getTime() - new Date(a.updatedAt!).getTime());
  }

  async createResume(resumeData: InsertResume): Promise<Resume> {
    const id = randomUUID();
    const resume: Resume = {
      id,
      ...resumeData,
      atsScore: null,
      atsAttempts: 0,
      isPublic: false,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.resumes.set(id, resume);
    return resume;
  }

  async updateResume(id: string, updates: Partial<InsertResume & { atsScore?: number | null; atsAttempts?: number }>): Promise<Resume | undefined> {
    const resume = this.resumes.get(id);
    if (!resume) return undefined;

    const updatedResume: Resume = {
      ...resume,
      ...updates,
      updatedAt: new Date(),
    };
    this.resumes.set(id, updatedResume);
    return updatedResume;
  }

  async deleteResume(id: string): Promise<boolean> {
    const existed = this.resumes.has(id);
    if (existed) {
      this.resumes.delete(id);
      // Also delete related ATS analyses
      const analysesToDelete = Array.from(this.atsAnalyses.entries())
        .filter(([, analysis]) => analysis.resumeId === id)
        .map(([analysisId]) => analysisId);

      analysesToDelete.forEach(analysisId => this.atsAnalyses.delete(analysisId));
    }
    return existed;
  }

  async searchResumes(userId: string, searchTerm: string): Promise<Resume[]> {
    const userResumes = await this.getUserResumes(userId);
    const lowercaseSearch = searchTerm.toLowerCase();

    return userResumes.filter(resume =>
      resume.title.toLowerCase().includes(lowercaseSearch) ||
      resume.templateId.toLowerCase().includes(lowercaseSearch) ||
      (typeof resume.content === 'object' && 
       JSON.stringify(resume.content).toLowerCase().includes(lowercaseSearch))
    );
  }

  async getResumesByTemplate(userId: string, templateId: string): Promise<Resume[]> {
    const userResumes = await this.getUserResumes(userId);
    return userResumes.filter(resume => resume.templateId === templateId);
  }

  // ATS Analysis operations
  async createAtsAnalysis(analysisData: InsertAtsAnalysis): Promise<AtsAnalysis> {
    const id = randomUUID();
    const analysis: AtsAnalysis = {
      id,
      ...analysisData,
      keywords: analysisData.keywords ?? null,
      suggestions: analysisData.suggestions ?? null,
      createdAt: new Date(),
    };
    this.atsAnalyses.set(id, analysis);
    return analysis;
  }

  async getResumeAtsHistory(resumeId: string): Promise<AtsAnalysis[]> {
    return Array.from(this.atsAnalyses.values())
      .filter(analysis => analysis.resumeId === resumeId)
      .sort((a, b) => new Date(b.createdAt!).getTime() - new Date(a.createdAt!).getTime())
      .slice(0, 10); // Return last 10 analyses
  }
}

export const storage = new MemStorage();

