import jsPDF from "jspdf";
import { ResumeContent } from "@shared/schema";

export class PdfExportService {
  private static readonly PAGE_WIDTH = 210; // A4 width in mm
  private static readonly PAGE_HEIGHT = 297; // A4 height in mm
  private static readonly MARGIN = 20;
  private static readonly LINE_HEIGHT = 6;

  static async exportResume(
    content: ResumeContent,
    templateId: string,
    title: string
  ): Promise<Blob> {
    const pdf = new jsPDF();

    // Set up fonts and colors
    pdf.setFont("helvetica", "normal");
    pdf.setTextColor(33, 37, 41); // Dark gray

    let yPosition = this.MARGIN;

    // Header with personal information
    yPosition = this.renderHeader(pdf, content.personalInfo, yPosition);

    // Professional Summary
    if (content.summary) {
      yPosition = this.renderSection(pdf, "Professional Summary", content.summary, yPosition);
    }

    // Work Experience
    if (content.experience.length > 0) {
      yPosition = this.renderExperience(pdf, content.experience, yPosition);
    }

    // Education
    if (content.education.length > 0) {
      yPosition = this.renderEducation(pdf, content.education, yPosition);
    }

    // Skills
    if (content.skills.length > 0) {
      yPosition = this.renderSkills(pdf, content.skills, yPosition);
    }

    // Projects (if any)
    if (content.projects && content.projects.length > 0) {
      yPosition = this.renderProjects(pdf, content.projects, yPosition);
    }

    // Certifications (if any)
    if (content.certifications && content.certifications.length > 0) {
      yPosition = this.renderCertifications(pdf, content.certifications, yPosition);
    }

    return pdf.output("blob");
  }

  private static renderHeader(pdf: jsPDF, personalInfo: ResumeContent["personalInfo"], yPosition: number): number {
    const { firstName, lastName, email, phone, location, website, linkedin } = personalInfo;

    // Name
    pdf.setFontSize(20);
    pdf.setFont("helvetica", "bold");
    const fullName = `${firstName} ${lastName}`;
    pdf.text(fullName, this.MARGIN, yPosition);
    yPosition += 10;

    // Contact information
    pdf.setFontSize(10);
    pdf.setFont("helvetica", "normal");
    pdf.setTextColor(100, 100, 100); // Gray

    const contactInfo = [email, phone, location, website, linkedin].filter(Boolean).join(" â€¢ ");
    pdf.text(contactInfo, this.MARGIN, yPosition);
    yPosition += 15;

    // Reset color
    pdf.setTextColor(33, 37, 41);

    return yPosition;
  }

  private static renderSection(pdf: jsPDF, title: string, content: string, yPosition: number): number {
    // Check if we need a new page
    if (yPosition > this.PAGE_HEIGHT - 50) {
      pdf.addPage();
      yPosition = this.MARGIN;
    }

    // Section title
    pdf.setFontSize(14);
    pdf.setFont("helvetica", "bold");
    pdf.text(title, this.MARGIN, yPosition);
    yPosition += 8;

    // Section content
    pdf.setFontSize(10);
    pdf.setFont("helvetica", "normal");
    const lines = pdf.splitTextToSize(content, this.PAGE_WIDTH - 2 * this.MARGIN);
    pdf.text(lines, this.MARGIN, yPosition);
    yPosition += lines.length * this.LINE_HEIGHT + 10;

    return yPosition;
  }

  private static renderExperience(pdf: jsPDF, experience: ResumeContent["experience"], yPosition: number): number {
    yPosition = this.renderSectionHeader(pdf, "Work Experience", yPosition);

    for (const exp of experience) {
      // Check if we need a new page
      if (yPosition > this.PAGE_HEIGHT - 80) {
        pdf.addPage();
        yPosition = this.MARGIN;
      }

      // Job title and company
      pdf.setFontSize(12);
      pdf.setFont("helvetica", "bold");
      pdf.text(`${exp.position} â€¢ ${exp.company}`, this.MARGIN, yPosition);
      yPosition += 6;

      // Dates and location
      pdf.setFontSize(9);
      pdf.setFont("helvetica", "normal");
      pdf.setTextColor(100, 100, 100);
      const dateRange = exp.current ? `${exp.startDate} - Present` : `${exp.startDate} - ${exp.endDate}`;
      pdf.text(`${dateRange} â€¢ ${exp.location}`, this.MARGIN, yPosition);
      yPosition += 8;

      // Description
      pdf.setFontSize(10);
      pdf.setTextColor(33, 37, 41);
      if (exp.description) {
        const descLines = pdf.splitTextToSize(exp.description, this.PAGE_WIDTH - 2 * this.MARGIN - 10);
        pdf.text(descLines, this.MARGIN + 5, yPosition);
        yPosition += descLines.length * this.LINE_HEIGHT + 2;
      }

      // Achievements
      if (exp.achievements.length > 0) {
        for (const achievement of exp.achievements) {
          const achLines = pdf.splitTextToSize(`â€¢ ${achievement}`, this.PAGE_WIDTH - 2 * this.MARGIN - 10);
          pdf.text(achLines, this.MARGIN + 5, yPosition);
          yPosition += achLines.length * this.LINE_HEIGHT;
        }
      }

      yPosition += 8;
    }

    return yPosition;
  }

  private static renderEducation(pdf: jsPDF, education: ResumeContent["education"], yPosition: number): number {
    yPosition = this.renderSectionHeader(pdf, "Education", yPosition);

    for (const edu of education) {
      if (yPosition > this.PAGE_HEIGHT - 60) {
        pdf.addPage();
        yPosition = this.MARGIN;
      }

      // Degree and school
      pdf.setFontSize(12);
      pdf.setFont("helvetica", "bold");
      pdf.text(`${edu.degree} â€¢ ${edu.school}`, this.MARGIN, yPosition);
      yPosition += 6;

      // Date and location
      pdf.setFontSize(9);
      pdf.setFont("helvetica", "normal");
      pdf.setTextColor(100, 100, 100);
      pdf.text(`${edu.graduationDate} â€¢ ${edu.location}`, this.MARGIN, yPosition);
      yPosition += 6;

      // GPA if present
      if (edu.gpa) {
        pdf.setTextColor(33, 37, 41);
        pdf.text(`GPA: ${edu.gpa}`, this.MARGIN, yPosition);
        yPosition += 6;
      }

      yPosition += 6;
    }

    return yPosition;
  }

  private static renderSkills(pdf: jsPDF, skills: ResumeContent["skills"], yPosition: number): number {
    yPosition = this.renderSectionHeader(pdf, "Skills", yPosition);

    for (const skillGroup of skills) {
      if (yPosition > this.PAGE_HEIGHT - 40) {
        pdf.addPage();
        yPosition = this.MARGIN;
      }

      // Skill category
      pdf.setFontSize(11);
      pdf.setFont("helvetica", "bold");
      pdf.text(`${skillGroup.category}:`, this.MARGIN, yPosition);

      // Skills list
      pdf.setFont("helvetica", "normal");
      const skillsText = skillGroup.items.join(", ");
      const skillsLines = pdf.splitTextToSize(skillsText, this.PAGE_WIDTH - 2 * this.MARGIN - 60);
      pdf.text(skillsLines, this.MARGIN + 60, yPosition);
      yPosition += Math.max(6, skillsLines.length * this.LINE_HEIGHT) + 4;
    }

    return yPosition + 6;
  }

  private static renderProjects(pdf: jsPDF, projects: ResumeContent["projects"], yPosition: number): number {
    yPosition = this.renderSectionHeader(pdf, "Projects", yPosition);

    if (!projects) return yPosition;

    for (const project of projects) {
      if (yPosition > this.PAGE_HEIGHT - 60) {
        pdf.addPage();
        yPosition = this.MARGIN;
      }

      // Project name
      pdf.setFontSize(12);
      pdf.setFont("helvetica", "bold");
      pdf.text(project.name, this.MARGIN, yPosition);
      yPosition += 6;

      // Description
      pdf.setFontSize(10);
      pdf.setFont("helvetica", "normal");
      const descLines = pdf.splitTextToSize(project.description, this.PAGE_WIDTH - 2 * this.MARGIN);
      pdf.text(descLines, this.MARGIN, yPosition);
      yPosition += descLines.length * this.LINE_HEIGHT + 2;

      // Technologies
      pdf.setFontSize(9);
      pdf.setTextColor(100, 100, 100);
      pdf.text(`Technologies: ${project.technologies.join(", ")}`, this.MARGIN, yPosition);
      yPosition += 10;

      pdf.setTextColor(33, 37, 41);
    }

    return yPosition;
  }

  private static renderCertifications(pdf: jsPDF, certifications: ResumeContent["certifications"], yPosition: number): number {
    yPosition = this.renderSectionHeader(pdf, "Certifications", yPosition);

    if (!certifications) return yPosition;

    for (const cert of certifications) {
      if (yPosition > this.PAGE_HEIGHT - 30) {
        pdf.addPage();
        yPosition = this.MARGIN;
      }

      pdf.setFontSize(11);
      pdf.setFont("helvetica", "bold");
      pdf.text(`${cert.name} â€¢ ${cert.issuer}`, this.MARGIN, yPosition);

      pdf.setFontSize(9);
      pdf.setFont("helvetica", "normal");
      pdf.setTextColor(100, 100, 100);
      pdf.text(cert.date, this.PAGE_WIDTH - this.MARGIN - 30, yPosition);

      yPosition += 8;
      pdf.setTextColor(33, 37, 41);
    }

    return yPosition;
  }

  private static renderSectionHeader(pdf: jsPDF, title: string, yPosition: number): number {
    if (yPosition > this.PAGE_HEIGHT - 40) {
      pdf.addPage();
      yPosition = this.MARGIN;
    }

    pdf.setFontSize(14);
    pdf.setFont("helvetica", "bold");
    pdf.setTextColor(33, 37, 41);
    pdf.text(title, this.MARGIN, yPosition);

    // Add underline
    pdf.setDrawColor(33, 37, 41);
    pdf.line(this.MARGIN, yPosition + 2, this.PAGE_WIDTH - this.MARGIN, yPosition + 2);

    return yPosition + 12;
  }
}

client/src/components/auth/auth-dialog.t.content:
import { useState } from "react";
import { Dialog, DialogContent } from "@/components/ui/dialog";
import { LoginForm } from "./login-form";
import { SignupForm } from "./signup-form";

interface AuthDialogProps {
  isOpen: boolean;
  onClose: () => void;
  initialMode?: "login" | "signup";
}

export function AuthDialog({ isOpen, onClose, initialMode = "login" }: AuthDialogProps) {
  const [mode, setMode] = useState<"login" | "signup">(initialMode);

  const toggleMode = () => {
    setMode(current => current === "login" ? "signup" : "login");
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md" data-testid="auth-dialog">
        {mode === "login" ? (
          <LoginForm 
            onToggleToSignup={toggleMode}
            onClose={onClose}
          />
        ) : (
          <SignupForm 
            onToggleToLogin={toggleMode}
            onClose={onClose}
          />
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/auth/login-form.ts.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Loader2, Mail, Lock } from "lucide-react";
import { useAuth } from "@/contexts/auth-context";

const loginSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

type LoginData = z.infer<typeof loginSchema>;

interface LoginFormProps {
  onToggleToSignup: () => void;
  onClose: () => void;
}

export function LoginForm({ onToggleToSignup, onClose }: LoginFormProps) {
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { signInWithEmail, signInWithGoogle } = useAuth();

  const form = useForm<LoginData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const onSubmit = async (data: LoginData) => {
    setIsLoading(true);
    setError("");

    try {
      await signInWithEmail(data.email, data.password);
      onClose();
    } catch (err: any) {
      setError(err.message || "Failed to sign in. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleSignIn = async () => {
    setIsLoading(true);
    setError("");

    try {
      await signInWithGoogle();
      onClose();
    } catch (err: any) {
      setError(err.message || "Failed to sign in with Google. Please try again.");
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-6" data-testid="login-form">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-foreground mb-2">Welcome Back</h2>
        <p className="text-muted-foreground">Sign in to continue building your resume</p>
      </div>

      {error && (
        <Alert variant="destructive" data-testid="login-error">
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
        <div className="space-y-2">
          <Label htmlFor="email">Email</Label>
          <div className="relative">
            <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
            <Input
              id="email"
              type="email"
              placeholder="Enter your email"
              className="pl-10"
              data-testid="input-email"
              {...form.register("email")}
            />
          </div>
          {form.formState.errors.email && (
            <p className="text-sm text-destructive">{form.formState.errors.email.message}</p>
          )}
        </div>

        <div className="space-y-2">
          <Label htmlFor="password">Password</Label>
          <div className="relative">
            <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
            <Input
              id="password"
              type="password"
              placeholder="Enter your password"
              className="pl-10"
              data-testid="input-password"
              {...form.register("password")}
            />
          </div>
          {form.formState.errors.password && (
            <p className="text-sm text-destructive">{form.formState.errors.password.message}</p>
          )}
        </div>

        <Button
          type="submit"
          className="w-full"
          disabled={isLoading}
          data-testid="button-signin"
        >
          {isLoading ? (
            <>
              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
              Signing In...
            </>
          ) : (
            "Sign In"
          )}
        </Button>
      </form>

      <div className="relative">
        <div className="absolute inset-0 flex items-center">
          <span className="w-full border-t border-border" />
        </div>
        <div className="relative flex justify-center text-xs uppercase">
          <span className="bg-background px-2 text-muted-foreground">Or continue with</span>
        </div>
      </div>

      <Button
        type="button"
        variant="outline"
        className="w-full"
        onClick={handleGoogleSignIn}
        disabled={isLoading}
        data-testid="button-google-signin"
      >
        <svg className="w-4 h-4 mr-2" viewBox="0 0 24 24">
          <path
            fill="currentColor"
            d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"
          />
          <path
            fill="currentColor"
            d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"
          />
          <path
            fill="currentColor"
            d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"
          />
          <path
            fill="currentColor"
            d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"
          />
        </svg>
        Continue with Google
      </Button>

      <div className="text-center">
        <p className="text-sm text-muted-foreground">
          Don't have an account?{" "}
          <button
            type="button"
            onClick={onToggleToSignup}
            className="text-primary hover:text-primary/80 font-medium"
            data-testid="link-signup"
          >
            Sign up
          </button>
        </p>
      </div>
    </div>
  );
}

client/src/components/auth/signup-form.t.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Loader2, Mail, Lock, User } from "lucide-react";
import { useAuth } from "@/contexts/auth-context";

const signupSchema = z.object({
  displayName: z.string().min(2, "Name must be at least 2 characters"),
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  confirmPassword: z.string(),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type SignupData = z.infer<typeof signupSchema>;

interface SignupFormProps {
  onToggleToLogin: () => void;
  onClose: () => void;
}

export function SignupForm({ onToggleToLogin, onClose }: SignupFormProps) {
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { signUpWithEmail, signInWithGoogle } = useAuth();

  const form = useForm<SignupData>({
    resolver: zodResolver(signupSchema),
    defaultValues: {
      displayName: "",
      email: "",
      password: "",
      confirmPassword: "",
    },
  });

  const onSubmit = async (data: SignupData) => {
    setIsLoading(true);
    setError("");

    try {
      await signUpWithEmail(data.email, data.password, data.displayName);
      onClose();
    } catch (err: any) {
      setError(err.message || "Failed to create account. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleSignIn = async () => {
    setIsLoading(true);
    setError("");

    try {
      await signInWithGoogle();
      onClose();
    } catch (err: any) {
      setError(err.message || "Failed to sign in with Google. Please try again.");
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-6" data-testid="signup-form">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-foreground mb-2">Create Account</h2>
        <p className="text-muted-foreground">Start building your professional resume</p>
      </div>

      {error && (
        <Alert variant="destructive" data-testid="signup-error">
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
        <div className="space-y-2">
          <Label htmlFor="displayName">Full Name</Label>
          <div className="relative">
            <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
            <Input
              id="displayName"
              type="text"
              placeholder="Enter your full name"
              className="pl-10"
              data-testid="input-displayname"
              {...form.register("displayName")}
            />
          </div>
          {form.formState.errors.displayName && (
            <p className="text-sm text-destructive">{form.formState.errors.displayName.message}</p>
          )}
        </div>

        <div className="space-y-2">
          <Label htmlFor="email">Email</Label>
          <div className="relative">
            <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
            <Input
              id="email"
              type="email"
              placeholder="Enter your email"
              className="pl-10"
              data-testid="input-email"
              {...form.register("email")}
            />
          </div>
          {form.formState.errors.email && (
            <p className="text-sm text-destructive">{form.formState.errors.email.message}</p>
          )}
        </div>

        <div className="space-y-2">
          <Label htmlFor="password">Password</Label>
          <div className="relative">
            <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
            <Input
              id="password"
              type="password"
              placeholder="Create a password"
              className="pl-10"
              data-testid="input-password"
              {...form.register("password")}
            />
          </div>
          {form.formState.errors.password && (
            <p className="text-sm text-destructive">{form.formState.errors.password.message}</p>
          )}
        </div>

        <div className="space-y-2">
          <Label htmlFor="confirmPassword">Confirm Password</Label>
          <div className="relative">
            <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
            <Input
              id="confirmPassword"
              type="password"
              placeholder="Confirm your password"
              className="pl-10"
              data-testid="input-confirm-password"
              {...form.register("confirmPassword")}
            />
          </div>
          {form.formState.errors.confirmPassword && (
            <p className="text-sm text-destructive">{form.formState.errors.confirmPassword.message}</p>
          )}
        </div>

        <Button
          type="submit"
          className="w-full"
          disabled={isLoading}
          data-testid="button-signup"
        >
          {isLoading ? (
            <>
              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
              Creating Account...
            </>
          ) : (
            "Create Account"
          )}
        </Button>
      </form>

      <div className="relative">
        <div className="absolute inset-0 flex items-center">
          <span className="w-full border-t border-border" />
        </div>
        <div className="relative flex justify-center text-xs uppercase">
          <span className="bg-background px-2 text-muted-foreground">Or continue with</span>
        </div>
      </div>

      <Button
        type="button"
        variant="outline"
        className="w-full"
        onClick={handleGoogleSignIn}
        disabled={isLoading}
        data-testid="button-google-signup"
      >
        <svg className="w-4 h-4 mr-2" viewBox="0 0 24 24">
          <path
            fill="currentColor"
            d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"
          />
          <path
            fill="currentColor"
            d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"
          />
          <path
            fill="currentColor"
            d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"
          />
          <path
            fill="currentColor"
            d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"
          />
        </svg>
        Continue with Google
      </Button>

      <div className="text-center">
        <p className="text-sm text-muted-foreground">
          Already have an account?{" "}
          <button
            type="button"
            onClick={onToggleToLogin}
            className="text-primary hover:text-primary/80 font-medium"
            data-testid="link-login"
          >
            Sign in
          </button>
        </p>
      </div>
    </div>
  );
}

