import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Input } from "@/components/ui/input";
import { ResumeCard } from "./resume-card";
import { useAuth } from "@/contexts/auth-context";
import { Resume } from "@shared/schema";
import { 
  Plus, 
  Search, 
  FileText, 
  BarChart3, 
  TrendingUp, 
  Clock,
  Filter,
  Grid,
  List,
  Star,
  Download,
  ChevronDown,
  User,
  LogOut
} from "lucide-react";
import { useLocation } from "wouter";

interface UserStats {
  totalResumes: number;
  avgAtsScore: number;
  templateUsage: Record<string, number>;
  mostRecentUpdate: string | null;
}

export function UserDashboard() {
  const [searchTerm, setSearchTerm] = useState("");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [sortBy, setSortBy] = useState<"updated" | "created" | "ats">("updated");
  const { currentUser, logout } = useAuth();
  const [, setLocation] = useLocation();

  // Fetch user's resumes
  const { data: resumes = [], isLoading: resumesLoading } = useQuery<Resume[]>({
    queryKey: ["/api/resumes", currentUser?.uid],
    enabled: !!currentUser,
  });

  // Fetch user analytics
  const { data: stats, isLoading: statsLoading } = useQuery<UserStats>({
    queryKey: ["/api/users", currentUser?.uid, "analytics"],
    enabled: !!currentUser,
  });

  const filteredResumes = resumes.filter((resume: Resume) =>
    resume.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const sortedResumes = [...filteredResumes].sort((a: Resume, b: Resume) => {
    switch (sortBy) {
      case "updated":
        return new Date(b.updatedAt!).getTime() - new Date(a.updatedAt!).getTime();
      case "created":
        return new Date(b.createdAt!).getTime() - new Date(a.createdAt!).getTime();
      case "ats":
        return (b.atsScore || 0) - (a.atsScore || 0);
      default:
        return 0;
    }
  });

  const getAtsScoreColor = (score?: number) => {
    if (!score) return "text-gray-400";
    if (score >= 80) return "text-green-600 dark:text-green-400";
    if (score >= 60) return "text-yellow-600 dark:text-yellow-400";
    return "text-red-600 dark:text-red-400";
  };

  const handleCreateResume = () => {
    setLocation("/experience-selector");
  };

  const handleEditResume = (resumeId: string) => {
    setLocation(`/resume-builder/${resumeId}`);
  };

  const handleDeleteResume = async (resumeId: string) => {
    // Implementation would make API call to delete resume
    console.log("Delete resume:", resumeId);
  };

  const handleDuplicateResume = async (resumeId: string) => {
    // Implementation would make API call to duplicate resume
    console.log("Duplicate resume:", resumeId);
  };

  const handleSignOut = async () => {
    try {
      await logout();
      setLocation("/");
    } catch (error) {
      console.error("Sign out error:", error);
    }
  };

  if (resumesLoading || statsLoading) {
    return (
      <div className="min-h-screen bg-background p-6">
        <div className="max-w-7xl mx-auto space-y-6">
          {/* Loading skeleton */}
          <div className="animate-pulse">
            <div className="h-8 bg-muted rounded w-1/4 mb-6" />
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
              {[1, 2, 3, 4].map((i) => (
                <div key={i} className="bg-card border border-border rounded-lg p-6">
                  <div className="h-4 bg-muted rounded w-3/4 mb-2" />
                  <div className="h-6 bg-muted rounded w-1/2" />
                </div>
              ))}
            </div>
            <div className="space-y-4">
              {[1, 2, 3].map((i) => (
                <div key={i} className="bg-card border border-border rounded-lg p-6">
                  <div className="h-4 bg-muted rounded w-1/2 mb-2" />
                  <div className="h-4 bg-muted rounded w-3/4" />
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="border-b border-border bg-card">
        <div className="max-w-7xl mx-auto px-6 py-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-foreground">
                Welcome back, {currentUser?.displayName || 'User'}!
              </h1>
              <p className="text-muted-foreground mt-1">
                Manage your resumes and track your job search progress
              </p>
            </div>
            <div className="flex items-center space-x-4">
              <Button 
                onClick={handleCreateResume}
                className="bg-primary text-primary-foreground hover:bg-primary/90"
                data-testid="button-create-resume"
              >
                <Plus className="w-4 h-4 mr-2" />
                Create Resume
              </Button>

              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="ghost" size="sm" data-testid="button-profile-menu">
                    <Avatar className="w-6 h-6 mr-2">
                      <AvatarImage src={currentUser?.photoURL || ""} />
                      <AvatarFallback>{currentUser?.displayName?.[0] || currentUser?.email?.[0] || "U"}</AvatarFallback>
                    </Avatar>
                    {currentUser?.displayName || "Profile"}
                    <ChevronDown className="w-4 h-4 ml-2" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end">
                  <DropdownMenuItem disabled>
                    <User className="w-4 h-4 mr-2" />
                    {currentUser?.email}
                  </DropdownMenuItem>
                  <DropdownMenuSeparator />
                  <DropdownMenuItem onClick={handleSignOut} data-testid="button-signout">
                    <LogOut className="w-4 h-4 mr-2" />
                    Sign Out
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-8 space-y-8">
        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <Card className="border-primary/20">
            <CardHeader className="pb-4">
              <CardTitle className="text-sm font-medium text-muted-foreground flex items-center">
                <FileText className="w-4 h-4 mr-2" />
                Total Resumes
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-3xl font-bold text-foreground">
                {stats?.totalResumes || 0}
              </div>
              <p className="text-sm text-muted-foreground mt-1">
                {resumes.length === 0 ? "Create your first resume" : "Keep building!"}
              </p>
            </CardContent>
          </Card>

          <Card className="border-secondary/20">
            <CardHeader className="pb-4">
              <CardTitle className="text-sm font-medium text-muted-foreground flex items-center">
                <BarChart3 className="w-4 h-4 mr-2" />
                Avg ATS Score
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className={`text-3xl font-bold ${getAtsScoreColor(stats?.avgAtsScore)}`}>
                {stats?.avgAtsScore || 0}%
              </div>
              <Progress value={stats?.avgAtsScore || 0} className="mt-2" />
            </CardContent>
          </Card>

          <Card className="border-accent/20">
            <CardHeader className="pb-4">
              <CardTitle className="text-sm font-medium text-muted-foreground flex items-center">
                <TrendingUp className="w-4 h-4 mr-2" />
                This Month
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-3xl font-bold text-foreground">
                {(resumes || []).filter((r: Resume) => {
                  const created = new Date(r.createdAt!);
                  const now = new Date();
                  return created.getMonth() === now.getMonth() && 
                         created.getFullYear() === now.getFullYear();
                }).length}
              </div>
              <p className="text-sm text-muted-foreground mt-1">
                Resumes created
              </p>
            </CardContent>
          </Card>

          <Card className="border-destructive/20">
            <CardHeader className="pb-4">
              <CardTitle className="text-sm font-medium text-muted-foreground flex items-center">
                <Clock className="w-4 h-4 mr-2" />
                Last Updated
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-lg font-semibold text-foreground">
                {stats?.mostRecentUpdate 
                  ? new Date(stats.mostRecentUpdate).toLocaleDateString()
                  : "Never"
                }
              </div>
              <p className="text-sm text-muted-foreground mt-1">
                Most recent edit
              </p>
            </CardContent>
          </Card>
        </div>

        {/* Resumes Section */}
        <div>
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold text-foreground">Your Resumes</h2>

            <div className="flex items-center space-x-4">
              {/* Search */}
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <Input
                  placeholder="Search resumes..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 w-64"
                  data-testid="input-search-resumes"
                />
              </div>

              {/* Sort */}
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value as "updated" | "created" | "ats")}
                className="px-3 py-2 border border-border rounded-lg bg-background text-foreground"
                data-testid="select-sort-by"
              >
                <option value="updated">Last Updated</option>
                <option value="created">Date Created</option>
                <option value="ats">ATS Score</option>
              </select>

              {/* View Mode */}
              <div className="flex border border-border rounded-lg">
                <Button
                  variant={viewMode === "grid" ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setViewMode("grid")}
                  data-testid="button-grid-view"
                >
                  <Grid className="w-4 h-4" />
                </Button>
                <Button
                  variant={viewMode === "list" ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setViewMode("list")}
                  data-testid="button-list-view"
                >
                  <List className="w-4 h-4" />
                </Button>
              </div>
            </div>
          </div>

          {/* Empty State */}
          {sortedResumes.length === 0 && (
            <Card className="border-dashed border-2 border-border">
              <CardContent className="flex flex-col items-center justify-center py-16">
                <div className="w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center mb-4">
                  <FileText className="w-8 h-8 text-primary" />
                </div>
                <h3 className="text-xl font-semibold text-foreground mb-2">
                  {searchTerm ? "No resumes found" : "No resumes yet"}
                </h3>
                <p className="text-muted-foreground text-center mb-6 max-w-md">
                  {searchTerm 
                    ? "Try adjusting your search terms or create a new resume"
                    : "Create your first professional resume with our AI-powered builder and ATS optimization"
                  }
                </p>
                <Button 
                  onClick={handleCreateResume}
                  className="bg-primary text-primary-foreground hover:bg-primary/90"
                  data-testid="button-create-first-resume"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  Create Your First Resume
                </Button>
              </CardContent>
            </Card>
          )}

          {/* Resumes Grid/List */}
          {sortedResumes.length > 0 && (
            <div className={
              viewMode === "grid" 
                ? "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" 
                : "space-y-4"
            }>
              {sortedResumes.map((resume: Resume) => (
                <ResumeCard
                  key={resume.id}
                  resume={resume}
                  viewMode={viewMode}
                  onEdit={() => handleEditResume(resume.id)}
                  onDelete={() => handleDeleteResume(resume.id)}
                  onDuplicate={() => handleDuplicateResume(resume.id)}
                />
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/components/resume/ats-scorer-.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { ScrollArea } from "@/components/ui/scroll-area";
import { ResumeContent, AtsFeedback } from "@shared/schema";
import { AtsScorer } from "@/services/ats-scorer";
import {
  BarChart3,
  Target,
  AlertCircle,
  CheckCircle,
  TrendingUp,
  FileText,
  Clock,
  Lightbulb,
  Zap,
  RefreshCw
} from "lucide-react";

interface AtsScorerPanelProps {
  content: ResumeContent;
  attemptsUsed: number;
  maxAttempts: number;
  onAnalyze: (feedback: AtsFeedback) => void;
  className?: string;
}

export function AtsScorerPanel({ 
  content, 
  attemptsUsed, 
  maxAttempts,
  onAnalyze,
  className = "" 
}: AtsScorerPanelProps) {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [feedback, setFeedback] = useState<AtsFeedback | null>(null);
  const [showDetails, setShowDetails] = useState(false);

  const remainingAttempts = maxAttempts - attemptsUsed;
  const canAnalyze = remainingAttempts > 0 && !isAnalyzing;

  const handleAnalyze = async () => {
    if (!canAnalyze) return;

    setIsAnalyzing(true);
    try {
      // Simulate API delay for analysis
      await new Promise(resolve => setTimeout(resolve, 2000));

      const analysis = AtsScorer.analyzeResume(content);
      setFeedback(analysis);
      setShowDetails(true);
      onAnalyze(analysis);
    } catch (error) {
      console.error('ATS Analysis failed:', error);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const getScoreColor = (score: number) => {
    if (score >= 80) return "text-green-600 dark:text-green-400";
    if (score >= 60) return "text-yellow-600 dark:text-yellow-400";
    return "text-red-600 dark:text-red-400";
  };

  const getScoreLabel = (score: number) => {
    if (score >= 80) return "Excellent";
    if (score >= 60) return "Good";
    return "Needs Work";
  };

  const getProgressColor = (score: number) => {
    if (score >= 80) return "bg-green-500";
    if (score >= 60) return "bg-yellow-500";
    return "bg-red-500";
  };

  return (
    <div className={`h-full flex flex-col bg-background ${className}`}>
      {/* Header */}
      <div className="p-6 border-b border-border bg-card">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-bold text-foreground flex items-center">
            <BarChart3 className="w-6 h-6 mr-2 text-primary" />
            ATS Scorer
          </h2>

          <Badge 
            variant={remainingAttempts > 0 ? "default" : "destructive"}
            className="text-sm"
          >
            {remainingAttempts}/{maxAttempts} attempts remaining
          </Badge>
        </div>

        {/* Analysis Button */}
        <Button
          onClick={handleAnalyze}
          disabled={!canAnalyze}
          className="w-full bg-primary text-primary-foreground hover:bg-primary/90 disabled:opacity-50"
          size="lg"
          data-testid="button-analyze-ats"
        >
          {isAnalyzing ? (
            <>
              <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
              Analyzing Resume...
            </>
          ) : (
            <>
              <Zap className="w-4 h-4 mr-2" />
              Analyze ATS Score
            </>
          )}
        </Button>

        {remainingAttempts === 0 && (
          <Alert className="mt-4">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>
              You've used all your ATS analysis attempts for this resume. 
              Consider upgrading for unlimited analyses.
            </AlertDescription>
          </Alert>
        )}
      </div>

      {/* Analysis Results */}
      <ScrollArea className="flex-1">
        <div className="p-6">
          {!feedback && !isAnalyzing && (
            <div className="text-center py-12">
              <div className="w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <Target className="w-8 h-8 text-primary" />
              </div>
              <h3 className="text-lg font-semibold text-foreground mb-2">
                Ready to Analyze
              </h3>
              <p className="text-muted-foreground">
                Click "Analyze ATS Score" to get detailed feedback on your resume's 
                compatibility with Applicant Tracking Systems.
              </p>
            </div>
          )}

          {isAnalyzing && (
            <div className="text-center py-12">
              <div className="w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <BarChart3 className="w-8 h-8 text-primary animate-pulse" />
              </div>
              <h3 className="text-lg font-semibold text-foreground mb-2">
                Analyzing Your Resume
              </h3>
              <p className="text-muted-foreground mb-4">
                Our AI is evaluating your resume against ATS standards...
              </p>
              <Progress value={75} className="w-48 mx-auto" />
            </div>
          )}

          {feedback && (
            <div className="space-y-6">
              {/* Overall Score */}
              <Card className="border-2 border-primary/20">
                <CardHeader className="pb-4">
                  <CardTitle className="flex items-center justify-between">
                    <span>Overall ATS Score</span>
                    <span className={`text-3xl font-bold ${getScoreColor(feedback.overall.score)}`}>
                      {feedback.overall.score}%
                    </span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <Progress 
                    value={feedback.overall.score} 
                    className="mb-4"
                  />
                  <div className="flex items-center justify-between">
                    <span className={`text-lg font-semibold ${getScoreColor(feedback.overall.score)}`}>
                      {getScoreLabel(feedback.overall.score)}
                    </span>
                    <Badge className={getScoreColor(feedback.overall.score).replace('text-', 'bg-').replace(' dark:text-', ' dark:bg-').replace(/-(400|600)/, '-100 $&')}>
                      {feedback.overall.score >= 80 ? 'Excellent' : 
                       feedback.overall.score >= 60 ? 'Good' : 'Needs Work'}
                    </Badge>
                  </div>
                  <p className="text-muted-foreground mt-2">
                    {feedback.overall.message}
                  </p>
                </CardContent>
              </Card>

              {/* Detailed Analysis */}
              <Tabs defaultValue="breakdown" className="w-full">
                <TabsList className="grid w-full grid-cols-3">
                  <TabsTrigger value="breakdown">Score Breakdown</TabsTrigger>
                  <TabsTrigger value="keywords">Keywords</TabsTrigger>
                  <TabsTrigger value="suggestions">Suggestions</TabsTrigger>
                </TabsList>

                <TabsContent value="breakdown" className="space-y-4">
                  {/* Keywords Score */}
                  <Card>
                    <CardContent className="pt-6">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-semibold flex items-center">
                          <Target className="w-4 h-4 mr-2 text-primary" />
                          Keywords
                        </h4>
                        <span className={`font-bold ${getScoreColor(feedback.sections.keywords.score)}`}>
                          {feedback.sections.keywords.score}%
                        </span>
                      </div>
                      <Progress value={feedback.sections.keywords.score} className="mb-2" />
                      <p className="text-sm text-muted-foreground">
                        Found {feedback.sections.keywords.found.length} relevant keywords
                      </p>
                    </CardContent>
                  </Card>

                  {/* Format Score */}
                  <Card>
                    <CardContent className="pt-6">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-semibold flex items-center">
                          <FileText className="w-4 h-4 mr-2 text-primary" />
                          Format
                        </h4>
                        <span className={`font-bold ${getScoreColor(feedback.sections.format.score)}`}>
                          {feedback.sections.format.score}%
                        </span>
                      </div>
                      <Progress value={feedback.sections.format.score} className="mb-2" />
                      <p className="text-sm text-muted-foreground">
                        {feedback.sections.format.issues.length === 0 
                          ? "Format looks good!" 
                          : `${feedback.sections.format.issues.length} formatting issues detected`
                        }
                      </p>
                    </CardContent>
                  </Card>

                  {/* Content Score */}
                  <Card>
                    <CardContent className="pt-6">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-semibold flex items-center">
                          <TrendingUp className="w-4 h-4 mr-2 text-primary" />
                          Content
                        </h4>
                        <span className={`font-bold ${getScoreColor(feedback.sections.content.score)}`}>
                          {feedback.sections.content.score}%
                        </span>
                      </div>
                      <Progress value={feedback.sections.content.score} className="mb-2" />
                      <p className="text-sm text-muted-foreground">
                        Content quality and structure assessment
                      </p>
                    </CardContent>
                  </Card>

                  {/* Length Score */}
                  <Card>
                    <CardContent className="pt-6">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-semibold flex items-center">
                          <Clock className="w-4 h-4 mr-2 text-primary" />
                          Length
                        </h4>
                        <span className={`font-bold ${getScoreColor(feedback.sections.length.score)}`}>
                          {feedback.sections.length.score}%
                        </span>
                      </div>
                      <Progress value={feedback.sections.length.score} className="mb-2" />
                      <p className="text-sm text-muted-foreground">
                        {feedback.sections.length.wordCount} words â€¢ {feedback.sections.length.ideal}
                      </p>
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="keywords" className="space-y-4">
                  {/* Found Keywords */}
                  <Card>
                    <CardHeader>
                      <CardTitle className="text-base flex items-center text-green-600 dark:text-green-400">
                        <CheckCircle className="w-4 h-4 mr-2" />
                        Found Keywords ({feedback.sections.keywords.found.length})
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="flex flex-wrap gap-2">
                        {feedback.sections.keywords.found.map((keyword, index) => (
                          <Badge key={index} variant="secondary" className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
                            {keyword}
                          </Badge>
                        ))}
                      </div>
                    </CardContent>
                  </Card>

                  {/* Missing Keywords */}
                  {feedback.sections.keywords.missing.length > 0 && (
                    <Card>
                      <CardHeader>
                        <CardTitle className="text-base flex items-center text-amber-600 dark:text-amber-400">
                          <AlertCircle className="w-4 h-4 mr-2" />
                          Missing Keywords ({feedback.sections.keywords.missing.length})
                        </CardTitle>
                      </CardHeader>
                      <CardContent>
                        <div className="flex flex-wrap gap-2">
                          {feedback.sections.keywords.missing.map((keyword, index) => (
                            <Badge key={index} variant="outline" className="border-amber-300 text-amber-700 dark:border-amber-700 dark:text-amber-300">
                              {keyword}
                            </Badge>
                          ))}
                        </div>
                      </CardContent>
                    </Card>
                  )}
                </TabsContent>

                <TabsContent value="suggestions" className="space-y-4">
                  {/* All Suggestions */}
                  <div className="space-y-4">
                    {feedback.sections.keywords.suggestions.length > 0 && (
                      <Card>
                        <CardHeader>
                          <CardTitle className="text-base flex items-center">
                            <Lightbulb className="w-4 h-4 mr-2 text-amber-500" />
                            Keyword Suggestions
                          </CardTitle>
                        </CardHeader>
                        <CardContent>
                          <ul className="space-y-2">
                            {feedback.sections.keywords.suggestions.map((suggestion, index) => (
                              <li key={index} className="flex items-start space-x-2">
                                <div className="w-1.5 h-1.5 bg-primary rounded-full mt-2 flex-shrink-0" />
                                <span className="text-sm">{suggestion}</span>
                              </li>
                            ))}
                          </ul>
                        </CardContent>
                      </Card>
                    )}

                    {feedback.sections.format.suggestions.length > 0 && (
                      <Card>
                        <CardHeader>
                          <CardTitle className="text-base flex items-center">
                            <FileText className="w-4 h-4 mr-2 text-primary" />
                            Format Suggestions
                          </CardTitle>
                        </CardHeader>
                        <CardContent>
                          <ul className="space-y-2">
                            {feedback.sections.format.suggestions.map((suggestion, index) => (
                              <li key={index} className="flex items-start space-x-2">
                                <div className="w-1.5 h-1.5 bg-primary rounded-full mt-2 flex-shrink-0" />
                                <span className="text-sm">{suggestion}</span>
                              </li>
                            ))}
                          </ul>
                        </CardContent>
                      </Card>
                    )}

                    {feedback.sections.content.suggestions.length > 0 && (
                      <Card>
                        <CardHeader>
                          <CardTitle className="text-base flex items-center">
                            <TrendingUp className="w-4 h-4 mr-2 text-secondary" />
                            Content Suggestions
                          </CardTitle>
                        </CardHeader>
                        <CardContent>
                          <ul className="space-y-2">
                            {feedback.sections.content.suggestions.map((suggestion, index) => (
                              <li key={index} className="flex items-start space-x-2">
                                <div className="w-1.5 h-1.5 bg-secondary rounded-full mt-2 flex-shrink-0" />
                                <span className="text-sm">{suggestion}</span>
                              </li>
                            ))}
                          </ul>
                        </CardContent>
                      </Card>
                    )}

                    {feedback.sections.length.suggestions.length > 0 && (
                      <Card>
                        <CardHeader>
                          <CardTitle className="text-base flex items-center">
                            <Clock className="w-4 h-4 mr-2 text-accent" />
                            Length Suggestions
                          </CardTitle>
                        </CardHeader>
                        <CardContent>
                          <ul className="space-y-2">
                            {feedback.sections.length.suggestions.map((suggestion, index) => (
                              <li key={index} className="flex items-start space-x-2">
                                <div className="w-1.5 h-1.5 bg-accent rounded-full mt-2 flex-shrink-0" />
                                <span className="text-sm">{suggestion}</span>
                              </li>
                            ))}
                          </ul>
                        </CardContent>
                      </Card>
                    )}
                  </div>
                </TabsContent>
              </Tabs>
            </div>
          )}
        </div>
      </ScrollArea>
    </div>
  );
}

client/src/components/resume/resume-edit.content:
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { ScrollArea } from "@/components/ui/scroll-area";
import { 
  Plus, 
  Trash2, 
  GripVertical, 
  Save, 
  User, 
  Briefcase, 
  GraduationCap, 
  Code, 
  Award,
  Globe,
  Languages
} from "lucide-react";
import { ResumeContent } from "@shared/schema";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { v4 as uuidv4 } from "uuid";

const personalInfoSchema = z.object({
  firstName: z.string().min(2, "First name must be at least 2 characters"),
  lastName: z.string().min(2, "Last name must be at least 2 characters"),
  email: z.string().email("Please enter a valid email address"),
  phone: z.string().min(10, "Please enter a valid phone number"),
  location: z.string().min(2, "Location is required"),
  website: z.string().url().optional().or(z.literal("")),
  linkedin: z.string().url().optional().or(z.literal("")),
  github: z.string().url().optional().or(z.literal("")),
});

interface ResumeEditorProps {
  content: ResumeContent;
  onChange: (content: ResumeContent) => void;
  onSave: () => void;
  isSaving: boolean;
}

export function ResumeEditor({ content, onChange, onSave, isSaving }: ResumeEditorProps) {
  const [activeTab, setActiveTab] = useState("personal");
  const [sectionOrder, setSectionOrder] = useState([
    "personal",
    "summary", 
    "experience",
    "education",
    "skills",
    "projects",
    "certifications",
    "languages"
  ]);

  const personalForm = useForm({
    resolver: zodResolver(personalInfoSchema),
    defaultValues: content.personalInfo,
  });

  useEffect(() => {
    personalForm.reset(content.personalInfo);
  }, [content.personalInfo, personalForm]);

  const handlePersonalInfoChange = (data: any) => {
    onChange({
      ...content,
      personalInfo: { ...content.personalInfo, ...data }
    });
  };

  const handleSummaryChange = (summary: string) => {
    onChange({ ...content, summary });
  };

  const addExperience = () => {
    const newExperience = {
      id: uuidv4(),
      position: "",
      company: "",
      location: "",
      startDate: "",
      endDate: "",
      current: false,
      description: "",
      achievements: []
    };
    onChange({
      ...content,
      experience: [...content.experience, newExperience]
    });
  };

  const updateExperience = (id: string, updates: any) => {
    onChange({
      ...content,
      experience: content.experience.map(exp => 
        exp.id === id ? { ...exp, ...updates } : exp
      )
    });
  };

  const removeExperience = (id: string) => {
    onChange({
      ...content,
      experience: content.experience.filter(exp => exp.id !== id)
    });
  };

  const addEducation = () => {
    const newEducation = {
      id: uuidv4(),
      degree: "",
      school: "",
      location: "",
      graduationDate: "",
      gpa: "",
      achievements: []
    };
    onChange({
      ...content,
      education: [...content.education, newEducation]
    });
  };

  const updateEducation = (id: string, updates: any) => {
    onChange({
      ...content,
      education: content.education.map(edu => 
        edu.id === id ? { ...edu, ...updates } : edu
      )
    });
  };

  const removeEducation = (id: string) => {
    onChange({
      ...content,
      education: content.education.filter(edu => edu.id !== id)
    });
  };

  const addSkillCategory = () => {
    const newCategory = {
      id: uuidv4(),
      category: "New Category",
      items: []
    };
    onChange({
      ...content,
      skills: [...content.skills, newCategory]
    });
  };

  const updateSkillCategory = (id: string, updates: any) => {
    onChange({
      ...content,
      skills: content.skills.map(skill => 
        skill.id === id ? { ...skill, ...updates } : skill
      )
    });
  };

  const removeSkillCategory = (id: string) => {
    onChange({
      ...content,
      skills: content.skills.filter(skill => skill.id !== id)
    });
  };

  const addProject = () => {
    if (!content.projects) {
      onChange({ ...content, projects: [] });
    }
    const newProject = {
      id: uuidv4(),
      name: "",
      description: "",
      technologies: [],
      url: "",
      github: ""
    };
    onChange({
      ...content,
      projects: [...(content.projects || []), newProject]
    });
  };

  const updateProject = (id: string, updates: any) => {
    if (!content.projects) return;
    onChange({
      ...content,
      projects: content.projects.map(project => 
        project.id === id ? { ...project, ...updates } : project
      )
    });
  };

  const removeProject = (id: string) => {
    if (!content.projects) return;
    onChange({
      ...content,
      projects: content.projects.filter(project => project.id !== id)
    });
  };

  const handleDragEnd = (result: any) => {
    if (!result.destination) return;

    const items = Array.from(sectionOrder);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setSectionOrder(items);
  };

  return (
    <div className="h-full flex flex-col bg-background">
      {/* Editor Header */}
      <div className="flex items-center justify-between p-4 border-b border-border bg-card">
        <h2 className="text-lg font-semibold text-foreground">Edit Resume Content</h2>
        <Button 
          onClick={onSave} 
          disabled={isSaving}
          className="bg-primary text-primary-foreground hover:bg-primary/90"
          data-testid="button-save-resume"
        >
          {isSaving ? (
            <>
              <div className="w-4 h-4 mr-2 border-2 border-white border-t-transparent rounded-full animate-spin" />
              Saving...
            </>
          ) : (
            <>
              <Save className="w-4 h-4 mr-2" />
              Save
            </>
          )}
        </Button>
      </div>

      {/* Section Reordering */}
      <div className="p-4 bg-muted/30 border-b border-border">
        <Label className="text-sm font-medium text-muted-foreground mb-2 block">
          Section Order (Drag to reorder)
        </Label>
        <DragDropContext onDragEnd={handleDragEnd}>
          <Droppable droppableId="sections" direction="horizontal">
            {(provided) => (
              <div
                {...provided.droppableProps}
                ref={provided.innerRef}
                className="flex flex-wrap gap-2"
              >
                {sectionOrder.map((section, index) => (
                  <Draggable key={section} draggableId={section} index={index}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="flex items-center space-x-1 bg-card border border-border rounded-lg px-3 py-1 cursor-move hover:bg-accent/50 transition-colors"
                      >
                        <GripVertical className="w-3 h-3 text-muted-foreground" />
                        <span className="text-sm capitalize">{section}</span>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
      </div>

      {/* Content Editor */}
      <ScrollArea className="flex-1">
        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full grid-cols-4 lg:grid-cols-8 m-4">
            <TabsTrigger value="personal" className="flex items-center space-x-1">
              <User className="w-4 h-4" />
              <span className="hidden sm:inline">Personal</span>
            </TabsTrigger>
            <TabsTrigger value="summary">Summary</TabsTrigger>
            <TabsTrigger value="experience" className="flex items-center space-x-1">
              <Briefcase className="w-4 h-4" />
              <span className="hidden sm:inline">Experience</span>
            </TabsTrigger>
            <TabsTrigger value="education" className="flex items-center space-x-1">
              <GraduationCap className="w-4 h-4" />
              <span className="hidden sm:inline">Education</span>
            </TabsTrigger>
            <TabsTrigger value="skills" className="flex items-center space-x-1">
              <Code className="w-4 h-4" />
              <span className="hidden sm:inline">Skills</span>
            </TabsTrigger>
            <TabsTrigger value="projects" className="flex items-center space-x-1">
              <Globe className="w-4 h-4" />
              <span className="hidden sm:inline">Projects</span>
            </TabsTrigger>
            <TabsTrigger value="certifications" className="flex items-center space-x-1">
              <Award className="w-4 h-4" />
              <span className="hidden sm:inline">Certs</span>
            </TabsTrigger>
            <TabsTrigger value="languages" className="flex items-center space-x-1">
              <Languages className="w-4 h-4" />
              <span className="hidden sm:inline">Languages</span>
            </TabsTrigger>
          </TabsList>

          {/* Personal Information */}
          <TabsContent value="personal" className="p-4">
            <Card>
              <CardHeader>
                <CardTitle>Personal Information</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="firstName">First Name</Label>
                    <Input
                      id="firstName"
                      {...personalForm.register("firstName")}
                      onChange={(e) => handlePersonalInfoChange({ firstName: e.target.value })}
                      data-testid="input-first-name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="lastName">Last Name</Label>
                    <Input
                      id="lastName"
                      {...personalForm.register("lastName")}
                      onChange={(e) => handlePersonalInfoChange({ lastName: e.target.value })}
                      data-testid="input-last-name"
                    />
                  </div>
                </div>
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    {...personalForm.register("email")}
                    onChange={(e) => handlePersonalInfoChange({ email: e.target.value })}
                    data-testid="input-email"
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="phone">Phone</Label>
                    <Input
                      id="phone"
                      {...personalForm.register("phone")}
                      onChange={(e) => handlePersonalInfoChange({ phone: e.target.value })}
                      data-testid="input-phone"
                    />
                  </div>
                  <div>
                    <Label htmlFor="location">Location</Label>
                    <Input
                      id="location"
                      {...personalForm.register("location")}
                      onChange={(e) => handlePersonalInfoChange({ location: e.target.value })}
                      data-testid="input-location"
                    />
                  </div>
                </div>
                <div className="grid grid-cols-3 gap-4">
                  <div>
                    <Label htmlFor="website">Website</Label>
                    <Input
                      id="website"
                      placeholder="https://..."
                      {...personalForm.register("website")}
                      onChange={(e) => handlePersonalInfoChange({ website: e.target.value })}
                      data-testid="input-website"
                    />
                  </div>
                  <div>
                    <Label htmlFor="linkedin">LinkedIn</Label>
                    <Input
                      id="linkedin"
                      placeholder="https://linkedin.com/in/..."
                      {...personalForm.register("linkedin")}
                      onChange={(e) => handlePersonalInfoChange({ linkedin: e.target.value })}
                      data-testid="input-linkedin"
                    />
                  </div>
                  <div>
                    <Label htmlFor="github">GitHub</Label>
                    <Input
                      id="github"
                      placeholder="https://github.com/..."
                      {...personalForm.register("github")}
                      onChange={(e) => handlePersonalInfoChange({ github: e.target.value })}
                      data-testid="input-github"
                    />
