import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  resumeTemplates, 
  getTemplatesByCategory,
  getTemplatesByExperienceLevel,
  ResumeTemplate 
} from "@/data/resume-templates";
import { ExperienceLevel, TemplateCategory } from "@shared/schema";
import { Star, Search, Palette, Briefcase, Sparkles } from "lucide-react";

interface TemplateSelectorProps {
  selectedTemplateId: string;
  onSelectTemplate: (templateId: string) => void;
  experienceLevel?: ExperienceLevel;
  className?: string;
}

export function TemplateSelector({ 
  selectedTemplateId, 
  onSelectTemplate, 
  experienceLevel,
  className = ""
}: TemplateSelectorProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<TemplateCategory | "all">("all");

  const getFilteredTemplates = (): ResumeTemplate[] => {
    let templates = resumeTemplates;

    // Filter by experience level if specified
    if (experienceLevel) {
      templates = getTemplatesByExperienceLevel(experienceLevel);
    }

    // Filter by category
    if (selectedCategory !== "all") {
      templates = templates.filter(template => template.category === selectedCategory);
    }

    // Filter by search term
    if (searchTerm) {
      templates = templates.filter(template =>
        template.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        template.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    return templates;
  };

  const filteredTemplates = getFilteredTemplates();

  const getCategoryIcon = (category: TemplateCategory) => {
    switch (category) {
      case "professional":
        return <Briefcase className="w-4 h-4" />;
      case "creative":
        return <Palette className="w-4 h-4" />;
      case "modern":
        return <Sparkles className="w-4 h-4" />;
      default:
        return null;
    }
  };

  const getCategoryColor = (category: TemplateCategory) => {
    switch (category) {
      case "professional":
        return "bg-primary/10 text-primary border-primary/20";
      case "creative":
        return "bg-accent/10 text-accent-foreground border-accent/20";
      case "modern":
        return "bg-secondary/10 text-secondary border-secondary/20";
      default:
        return "bg-muted";
    }
  };

  return (
    <div className={`h-full flex flex-col bg-background ${className}`}>
      {/* Header */}
      <div className="p-6 border-b border-border bg-card">
        <h2 className="text-2xl font-bold text-foreground mb-4">Choose Template</h2>

        {/* Search */}
        <div className="relative mb-4">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
          <Input
            placeholder="Search templates..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10"
            data-testid="input-template-search"
          />
        </div>

        {/* Category Filters */}
        <Tabs value={selectedCategory} onValueChange={(value) => setSelectedCategory(value as TemplateCategory | "all")}>
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="all" className="flex items-center space-x-2">
              <span>All</span>
              <Badge variant="secondary" className="ml-1 text-xs">
                {filteredTemplates.length}
              </Badge>
            </TabsTrigger>
            <TabsTrigger value="professional" className="flex items-center space-x-2">
              {getCategoryIcon("professional")}
              <span className="hidden sm:inline">Professional</span>
            </TabsTrigger>
            <TabsTrigger value="creative" className="flex items-center space-x-2">
              {getCategoryIcon("creative")}
              <span className="hidden sm:inline">Creative</span>
            </TabsTrigger>
            <TabsTrigger value="modern" className="flex items-center space-x-2">
              {getCategoryIcon("modern")}
              <span className="hidden sm:inline">Modern</span>
            </TabsTrigger>
          </TabsList>
        </Tabs>
      </div>

      {/* Templates Grid */}
      <ScrollArea className="flex-1">
        <div className="p-6">
          {experienceLevel && (
            <div className="mb-6 p-4 bg-primary/5 border border-primary/20 rounded-lg">
              <p className="text-sm text-primary font-medium">
                Showing templates optimized for {experienceLevel} level professionals
              </p>
            </div>
          )}

          {filteredTemplates.length === 0 ? (
            <div className="text-center py-12">
              <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
                <Search className="w-6 h-6 text-muted-foreground" />
              </div>
              <h3 className="text-lg font-semibold text-foreground mb-2">No templates found</h3>
              <p className="text-muted-foreground">
                Try adjusting your search terms or category filters
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredTemplates.map((template) => (
                <Card
                  key={template.id}
                  className={`cursor-pointer transition-all duration-200 hover:shadow-lg hover:-translate-y-1 ${
                    selectedTemplateId === template.id 
                      ? 'ring-2 ring-primary shadow-lg' 
                      : 'hover:ring-1 hover:ring-border'
                  }`}
                  onClick={() => onSelectTemplate(template.id)}
                  data-testid={`card-template-${template.id}`}
                >
                  {/* Template Preview */}
                  <div className={`aspect-[3/4] ${template.preview} p-4 rounded-t-lg relative`}>
                    {template.popular && (
                      <div className="absolute top-2 right-2">
                        <Badge className="bg-accent text-accent-foreground">
                          Popular
                        </Badge>
                      </div>
                    )}

                    <div 
                      className="bg-white dark:bg-white rounded-lg p-4 shadow-sm h-full flex flex-col justify-between"
                      style={{ color: '#000' }}
                    >
                      {/* Header */}
                      <div className="space-y-2">
                        <div 
                          className="h-3 rounded w-full"
                          style={{ backgroundColor: template.colors.primary }}
                        />
                        <div className="h-1.5 bg-gray-300 rounded w-3/4" />
                        <div className="h-1.5 bg-gray-300 rounded w-1/2" />
                      </div>

                      {/* Content sections */}
                      <div className="space-y-3 flex-1 mt-4">
                        <div className="space-y-1">
                          <div className="h-1 bg-gray-400 rounded w-1/4" />
                          <div className="h-1 bg-gray-300 rounded" />
                          <div className="h-1 bg-gray-300 rounded w-4/5" />
                          <div className="h-1 bg-gray-300 rounded w-3/5" />
                        </div>

                        <div className="space-y-1">
                          <div className="h-1 bg-gray-400 rounded w-1/3" />
                          <div className="h-1 bg-gray-300 rounded w-5/6" />
                          <div className="h-1 bg-gray-300 rounded w-2/3" />
                        </div>
                      </div>

                      {/* Footer accent */}
                      <div 
                        className="h-1 rounded w-full mt-4"
                        style={{ backgroundColor: template.colors.secondary }}
                      />
                    </div>
                  </div>

                  {/* Template Info */}
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between mb-2">
                      <h3 className="font-semibold text-foreground">{template.name}</h3>
                      <div className="flex items-center space-x-1">
                        <Star className="w-3 h-3 text-accent fill-accent" />
                        <span className="text-xs text-muted-foreground">{template.rating}</span>
                      </div>
                    </div>

                    <p className="text-sm text-muted-foreground mb-3 line-clamp-2">
                      {template.description}
                    </p>

                    <div className="flex items-center justify-between">
                      <Badge 
                        variant="outline" 
                        className={`text-xs ${getCategoryColor(template.category)}`}
                      >
                        {getCategoryIcon(template.category)}
                        <span className="ml-1 capitalize">{template.category}</span>
                      </Badge>

                      {selectedTemplateId === template.id && (
                        <Badge className="bg-primary text-primary-foreground">
                          Selected
                        </Badge>
                      )}
                    </div>

                    <div className="mt-3 flex flex-wrap gap-1">
                      {template.features.slice(0, 2).map((feature, index) => (
                        <Badge key={index} variant="secondary" className="text-xs">
                          {feature}
                        </Badge>
                      ))}
                      {template.features.length > 2 && (
                        <Badge variant="secondary" className="text-xs">
                          +{template.features.length - 2} more
                        </Badge>
                      )}
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </div>
      </ScrollArea>

      {/* Footer */}
      {selectedTemplateId && (
        <div className="p-4 border-t border-border bg-card">
          <div className="flex items-center justify-between">
            <div className="text-sm text-muted-foreground">
              Selected: {filteredTemplates.find(t => t.id === selectedTemplateId)?.name}
            </div>
            <Button
              onClick={() => onSelectTemplate(selectedTemplateId)}
              className="bg-primary text-primary-foreground hover:bg-primary/90"
              data-testid="button-use-template"
            >
              Use This Template
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/templates/template.content:
import { ResumeContent } from "@shared/schema";
import { getTemplate } from "@/data/resume-templates";

interface TemplateRendererProps {
  content: ResumeContent;
  templateId: string;
  className?: string;
}

export function TemplateRenderer({ content, templateId, className = "" }: TemplateRendererProps) {
  const template = getTemplate(templateId);

  if (!template) {
    return (
      <div className={`p-8 text-center ${className}`}>
        <p className="text-gray-500">Template not found</p>
      </div>
    );
  }

  const { personalInfo, summary, experience, education, skills, projects } = content;

  // Helper function to format dates
  const formatDateRange = (startDate: string, endDate: string, current: boolean) => {
    const start = startDate || "Present";
    const end = current ? "Present" : (endDate || "Present");
    return `${start} - ${end}`;
  };

  // Different template layouts based on template category
  if (template.category === "modern") {
    return (
      <div className={`bg-white p-8 font-sans text-gray-900 ${className}`} style={{ minHeight: '11in', width: '8.5in' }}>
        {/* Modern Template Layout */}
        <div className="flex">
          {/* Left Column - 1/3 width */}
          <div className="w-1/3 pr-6" style={{ backgroundColor: template.colors.primary, color: 'white', padding: '2rem 1.5rem', margin: '-2rem 0 -2rem -2rem' }}>
            {/* Personal Info */}
            <div className="mb-8">
              <h1 className="text-2xl font-bold mb-1">{personalInfo.firstName} {personalInfo.lastName}</h1>
              <div className="text-sm opacity-90 space-y-1">
                <div>{personalInfo.email}</div>
                <div>{personalInfo.phone}</div>
                <div>{personalInfo.location}</div>
                {personalInfo.linkedin && <div>{personalInfo.linkedin}</div>}
                {personalInfo.website && <div>{personalInfo.website}</div>}
              </div>
            </div>

            {/* Skills */}
            {skills.length > 0 && (
              <div className="mb-8">
                <h2 className="text-lg font-semibold mb-4">Skills</h2>
                {skills.map((skillGroup) => (
                  <div key={skillGroup.id} className="mb-4">
                    <h3 className="font-medium mb-2">{skillGroup.category}</h3>
                    <div className="text-sm opacity-90">
                      {skillGroup.items.map((skill, index) => (
                        <div key={index} className="mb-1">â€¢ {skill}</div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Right Column - 2/3 width */}
          <div className="w-2/3 pl-6">
            {/* Professional Summary */}
            {summary && (
              <div className="mb-8">
                <h2 className="text-xl font-semibold mb-3" style={{ color: template.colors.primary }}>Professional Summary</h2>
                <p className="text-sm leading-relaxed">{summary}</p>
              </div>
            )}

            {/* Experience */}
            {experience.length > 0 && (
              <div className="mb-8">
                <h2 className="text-xl font-semibold mb-4" style={{ color: template.colors.primary }}>Experience</h2>
                {experience.map((exp) => (
                  <div key={exp.id} className="mb-6">
                    <div className="flex justify-between items-start mb-2">
                      <div>
                        <h3 className="font-semibold text-lg">{exp.position}</h3>
                        <p className="font-medium" style={{ color: template.colors.secondary }}>{exp.company}</p>
                      </div>
                      <div className="text-sm text-gray-600">
                        {formatDateRange(exp.startDate, exp.endDate, exp.current)}
                      </div>
                    </div>
                    <p className="text-sm text-gray-700 mb-2">{exp.location}</p>
                    {exp.description && (
                      <p className="text-sm leading-relaxed mb-2">{exp.description}</p>
                    )}
                    {exp.achievements.length > 0 && (
                      <ul className="text-sm space-y-1">
                        {exp.achievements.map((achievement, index) => (
                          <li key={index} className="flex items-start">
                            <span className="mr-2">â€¢</span>
                            <span>{achievement}</span>
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                ))}
              </div>
            )}

            {/* Education */}
            {education.length > 0 && (
              <div className="mb-8">
                <h2 className="text-xl font-semibold mb-4" style={{ color: template.colors.primary }}>Education</h2>
                {education.map((edu) => (
                  <div key={edu.id} className="mb-4">
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="font-semibold">{edu.degree}</h3>
                        <p className="font-medium" style={{ color: template.colors.secondary }}>{edu.school}</p>
                        <p className="text-sm text-gray-700">{edu.location}</p>
                      </div>
                      <div className="text-sm text-gray-600">
                        {edu.graduationDate}
                      </div>
                    </div>
                    {edu.gpa && (
                      <p className="text-sm text-gray-700">GPA: {edu.gpa}</p>
                    )}
                  </div>
                ))}
              </div>
            )}

            {/* Projects */}
            {projects && projects.length > 0 && (
              <div className="mb-8">
                <h2 className="text-xl font-semibold mb-4" style={{ color: template.colors.primary }}>Projects</h2>
                {projects.map((project) => (
                  <div key={project.id} className="mb-4">
                    <h3 className="font-semibold">{project.name}</h3>
                    <p className="text-sm leading-relaxed mb-2">{project.description}</p>
                    <p className="text-sm text-gray-700">
                      <strong>Technologies:</strong> {project.technologies.join(", ")}
                    </p>
                    {project.url && (
                      <p className="text-sm text-gray-700">
                        <strong>URL:</strong> {project.url}
                      </p>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }

  // Professional/Classic Template (default)
  return (
    <div className={`bg-white p-8 font-sans text-gray-900 ${className}`} style={{ minHeight: '11in', width: '8.5in' }}>
      {/* Header */}
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold mb-2" style={{ color: template.colors.primary }}>
          {personalInfo.firstName} {personalInfo.lastName}
        </h1>
        <div className="text-sm text-gray-600 space-x-2">
          <span>{personalInfo.email}</span>
          <span>â€¢</span>
          <span>{personalInfo.phone}</span>
          <span>â€¢</span>
          <span>{personalInfo.location}</span>
          {personalInfo.linkedin && (
            <>
              <span>â€¢</span>
              <span>{personalInfo.linkedin}</span>
            </>
          )}
        </div>
      </div>

      {/* Professional Summary */}
      {summary && (
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-3 pb-2 border-b-2" style={{ borderColor: template.colors.primary }}>
            Professional Summary
          </h2>
          <p className="text-sm leading-relaxed">{summary}</p>
        </div>
      )}

      {/* Experience */}
      {experience.length > 0 && (
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-4 pb-2 border-b-2" style={{ borderColor: template.colors.primary }}>
            Work Experience
          </h2>
          {experience.map((exp) => (
            <div key={exp.id} className="mb-6">
              <div className="flex justify-between items-start mb-2">
                <div>
                  <h3 className="font-semibold text-lg">{exp.position}</h3>
                  <p className="font-medium" style={{ color: template.colors.secondary }}>{exp.company} â€¢ {exp.location}</p>
                </div>
                <div className="text-sm text-gray-600 text-right">
                  {formatDateRange(exp.startDate, exp.endDate, exp.current)}
                </div>
              </div>
              {exp.description && (
                <p className="text-sm leading-relaxed mb-2">{exp.description}</p>
              )}
              {exp.achievements.length > 0 && (
                <ul className="text-sm space-y-1">
                  {exp.achievements.map((achievement, index) => (
                    <li key={index} className="flex items-start">
                      <span className="mr-2">â€¢</span>
                      <span>{achievement}</span>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          ))}
        </div>
      )}

      {/* Education */}
      {education.length > 0 && (
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-4 pb-2 border-b-2" style={{ borderColor: template.colors.primary }}>
            Education
          </h2>
          {education.map((edu) => (
            <div key={edu.id} className="mb-4">
              <div className="flex justify-between items-start">
                <div>
                  <h3 className="font-semibold">{edu.degree}</h3>
                  <p className="font-medium" style={{ color: template.colors.secondary }}>
                    {edu.school} â€¢ {edu.location}
                  </p>
                  {edu.gpa && (
                    <p className="text-sm text-gray-700">GPA: {edu.gpa}</p>
                  )}
                </div>
                <div className="text-sm text-gray-600">
                  {edu.graduationDate}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Skills */}
      {skills.length > 0 && (
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-4 pb-2 border-b-2" style={{ borderColor: template.colors.primary }}>
            Skills
          </h2>
          {skills.map((skillGroup) => (
            <div key={skillGroup.id} className="mb-3">
              <div className="flex">
                <span className="font-semibold w-32 flex-shrink-0">{skillGroup.category}:</span>
                <span className="text-sm">{skillGroup.items.join(", ")}</span>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Projects */}
      {projects && projects.length > 0 && (
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-4 pb-2 border-b-2" style={{ borderColor: template.colors.primary }}>
            Projects
          </h2>
          {projects.map((project) => (
            <div key={project.id} className="mb-4">
              <h3 className="font-semibold">{project.name}</h3>
              <p className="text-sm leading-relaxed mb-1">{project.description}</p>
              <p className="text-sm text-gray-700">
                <strong>Technologies:</strong> {project.technologies.join(", ")}
              </p>
              {project.url && (
                <p className="text-sm text-gray-700">
                  <strong>URL:</strong> {project.url}
                </p>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

